import React, { createContext, useContext, useState } from 'react';

const QuizContext = createContext();

export function useQuizContext() {
  return useContext(QuizContext);
}

export function QuizProvider({ children }) {
  const [showQuizPage, setShowQuizPage] = useState(() => {
    const savedState = localStorage.getItem('showQuizPage');
    return savedState === 'true';
  });
  const [selectedMode, setSelectedMode] = useState(null);
  const [currentQuizId, setCurrentQuizId] = useState(null);
  const [quizStates, setQuizStates] = useState({});

  const updateQuizState = (uuid, newState) => {
    console.log(`Setting quizId to: ${uuid}`);
    setQuizStates(prevStates => ({
      ...prevStates,
      [uuid]: {
        ...prevStates[uuid],
        ...newState,
      },
    }));
  };

  // Функция для пометки блока как использованного
  const markBlockAsUsed = (quizId, categoryId, blockId) => {
    updateQuizState(quizId, prevState => {
      const updatedCategories = prevState.data.map(category => {
        if (category.id === categoryId) {
          return {
            ...category,
            blocks: category.blocks.map(block =>
              block.id === blockId ? { ...block, used: true } : block
            ),
          };
        }
        return category;
      });
      return { data: updatedCategories };
    });
  };

  return (
    <QuizContext.Provider value={{
      showQuizPage,
      setShowQuizPage,
      selectedMode,
      setSelectedMode,
      currentQuizId,
      setCurrentQuizId,
      quizStates,
      updateQuizState,
      markBlockAsUsed,
    }}>
      {children}
    </QuizContext.Provider>
  );
}
