import React, { useState, useEffect } from 'react';
import styles from './Settings.module.css';

function Settings({ onClose }) {
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'default');

  useEffect(() => {
    localStorage.setItem('theme', theme);
    document.body.setAttribute('data-theme', theme); // Устанавливаем атрибут для темы на уровне документа
  }, [theme]);

  const handleDefaultTheme = () => setTheme('default');
  const handleCustomTheme = () => setTheme('custom');

  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <button className={styles.tab}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <button className={styles.tab}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>
      <div className={styles.content}>
        <div className={styles.buttonContainer}>
          <button className={styles.button} onClick={handleDefaultTheme}>
          Default
          </button>
          <button className={styles.button} onClick={handleCustomTheme}>
            Custome
          </button>
        </div>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Закрыть</button>
    </div>
  );
}

export default Settings;
