import React, { useState } from 'react';
import styles from '../styles/Settings.module.css';

function Settings({ onClose, selectedMode, setSelectedMode }) {
  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <div className={styles.radioGroup}>
          <label>
            <input
              type="radio"
              name="mode"
              value="QAMode"
              checked={selectedMode === 'QAMode'}
              onChange={() => setSelectedMode('QAMode')}
            />
            QAMode
          </label>
          <label>
            <input
              type="radio"
              name="mode"
              value="SelectionMode"
              checked={selectedMode === 'SelectionMode'}
              onChange={() => setSelectedMode('SelectionMode')}
            />
            SelectionMode
          </label>
        </div>
      </div>
      <div className={styles.content}>
        <h2>Settings Content</h2>
        <p>Content for settings will be displayed here.</p>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Close</button>
    </div>
  );
}

export default Settings;
