import React from 'react';
import QAMode from '../QAMode/QAMode';
import SelectionMode from '../SelectionMode/SelectionMode';
import styles from './Modal.module.css';

const modeComponents = {
  1: QAMode,
  2: SelectionMode,
  // Добавьте другие режимы, если они есть
};

function Modal({
  block,
  categoryName,
  onClose,
  selectedMode,
  onSelectCategory,
}) {
  const ModeComponent = modeComponents[selectedMode];

  const handleCategorySelection = () => {
    onSelectCategory(block.categoryId, block.id);  // Сначала выполняем выбор категории
    onClose();  // Затем закрываем модальное окно
  };

  if (!block) {
    return null;
  }

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        {ModeComponent ? (
          <ModeComponent
            block={block}
            categoryName={categoryName}
            onSelectCategory={handleCategorySelection}  // Передаем обработчик выбора категории
          />
        ) : (
          <div>Unknown mode</div>
        )}
      </div>
    </div>
  );
}

export default Modal;
