import React, { useMemo, useState, useEffect } from 'react';
import ContentContainer from '../ContentContainer/ContentContainer';
import EndMessage from '../EndMessage/EndMessage';
import MenuModal from '../MenuModal/MenuModal';
import Modal from '../Modal/Modal';
import Settings from '../Settings/Settings';
import ConfettiAnimation from '../ConfettiAnimation/ConfettiAnimation'; // Добавляем компонент для конфетти
import styles from './QuizPage.module.css';
import { useQuizContext } from '../../context/QuizContext';
import PCImage from '../../images/PC_horizontal_1line_black.svg'; // Убедитесь, что путь к изображению верен  

function QuizPage() {
  const { quizStates, setShowQuizPage, currentQuizId, selectedMode, data, markBlockAsUsed } = useQuizContext();

  const currentQuizState = useMemo(() => quizStates[currentQuizId] || {}, [quizStates, currentQuizId]);
  const [isSettingsVisible, setIsSettingsVisible] = useState(false);
  const [selectedBlock, setSelectedBlock] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [confettiRunning, setConfettiRunning] = useState(false); // Стейт для анимации конфетти

  const handleBlockSelect = (block, category) => {
    setSelectedBlock(block);
    setSelectedCategory(category);
  };

  const handleCloseModal = () => {
    setSelectedBlock(null);
    setSelectedCategory(null);
  };

  const handleSelectCategory = (categoryId, blockId) => {
    markBlockAsUsed(currentQuizId, categoryId, blockId);
    
    // Проверяем, все ли блоки в категории использованы
    const categoryBlocks = selectedCategory.blocks;
    const allUsed = categoryBlocks.every((block) => {
      const usedBlocks = currentQuizState.usedBlocks?.[categoryId] || [];
      return usedBlocks.includes(block.id);
    });

    if (allUsed) {
      // Запускаем анимацию конфетти, если все блоки использованы
      setConfettiRunning(true);
      setTimeout(() => {
        setConfettiRunning(false);
      }, 5000);
    }

    handleCloseModal();  // Закрываем модальное окно после выбора категории
  };

  useEffect(() => {
    console.log('Data in QuizPage:', data);
  }, [data]);

  return (
    <div className={styles.quiz_page}>
      <ConfettiAnimation isRunning={confettiRunning} /> {/* Подключаем анимацию конфетти */}
      {data ? (
        <>
          <img src={PCImage} alt="PC horizontal line" className={styles.image} />
          <ContentContainer
            data={data}
            onBlockSelect={handleBlockSelect}
            usedBlocks={currentQuizState.usedBlocks || {}}
          />
          {selectedBlock && (
            <Modal
              block={selectedBlock}
              categoryName={selectedCategory?.name || 'Без категории'}
              onClose={handleCloseModal}
              selectedMode={selectedMode}
              onSelectCategory={handleSelectCategory} // Передаем управление выбором в QuizPage
            />
          )}
        </>
      ) : (
        <div>No data available.</div>
      )}
      <EndMessage />
      <MenuModal
        showSettings={() => setIsSettingsVisible(true)}
        showMainMenu={() => setShowQuizPage(false)}
      />
      {isSettingsVisible && <Settings onClose={() => setIsSettingsVisible(false)} />}
    </div>
  );
}

export default QuizPage;
