import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import Sidebar from './Sidebar';
import styles from '../styles/App.module.css';
import { QuizProvider, useQuizContext } from '../context/QuizContext';
import { loadUniqueUuids } from '../utils/loadJsonData'; // Импорт функции

function App() {
  const { showQuizPage, setShowQuizPage, setSelectedMode, setCurrentQuizId } = useQuizContext();
  const [quizData, setQuizData] = useState([]);

  useEffect(() => {
    const uniqueData = loadUniqueUuids(); // Загрузка данных
    setQuizData(uniqueData);
  }, []);

  const startQuiz = (mode, uuid) => {
    setSelectedMode(mode);
    setCurrentQuizId(uuid); // Установка текущего идентификатора викторины
    setShowQuizPage(true);
  };

  return (
    <Router>
      <div className={styles.container}>
        {!showQuizPage && <Sidebar />}
        <div className={showQuizPage ? styles.hidden : styles.content}>
          <Routes>
            <Route
              path="/"
              element={
                !showQuizPage ? (
                  quizData.map((data) => (
                    <QuizCard
                      key={data.uuid}
                      startQuiz={startQuiz}
                      mode={data.mode}
                      uuid={data.uuid}
                      category={data.categories[0]}
                    />
                  ))
                ) : null
              }
            />
          </Routes>
        </div>
        {showQuizPage && (
          <div className={styles.fullscreen}>
            <QuizPage 
              mode={selectedMode} 
              quizId={currentQuizId} // Передаем идентификатор и режим в QuizPage
            />
          </div>
        )}
      </div>
    </Router>
  );
}
