import React, { useState, useEffect } from 'react';
import SelectCategoryButton from '../SelectCategoryButton/SelectCategoryButton';
import styles from './SelectionMode.module.css';

function SelectionMode({
  block,
  categoryName,
  onSelectCategory,
  onClose, // Получаем функцию закрытия модального окна
}) {
  const [answerShown, setAnswerShown] = useState(false);

  useEffect(() => {
    setAnswerShown(false);
  }, [block]);

  const handleSelectCategoryInternal = () => {
    onSelectCategory(block.categoryId, block.id); // Выбор категории
    onClose(); // Закрываем модальное окно
  };

  return (
    <div className={styles.content}>
      <div className={styles.selectedInfo}>
        <span className={styles.infoCategoryName}>{categoryName}</span>
        <div className={styles.selectedNumber}>{block.id + 1}</div>
      </div>
      {/* Другие элементы */}
      <SelectCategoryButton onClick={handleSelectCategoryInternal} /> {/* Кнопка для выбора категории */}
    </div>
  );
}

export default SelectionMode;
