import React from 'react';
import QAMode from '../QAMode/QAMode';
import SelectionMode from '../SelectionMode/SelectionMode';
import WarningMessage from '../WarningMessage/WarningMessage';
import styles from './Modal.module.css';

const modeComponents = {
  1: QAMode,
  2: SelectionMode,
};

function Modal({
  block,
  categoryName,
  onClose,
  selectedMode,
  onSelectCategory,
  isWarningVisible,
  onTryAgain,
  onContinue,
  timerStarted, // Добавляем проп для таймера
  setTimerStarted, // Добавляем проп для управления таймером
}) {
  const ModeComponent = modeComponents[selectedMode];

  if (!block) {
    return null;
  }

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        {isWarningVisible ? (
          <WarningMessage onTryAgain={onTryAgain} onContinue={onContinue} />
        ) : (
          ModeComponent && (
            <ModeComponent
              block={block}
              categoryName={categoryName}
              onSelectCategory={onSelectCategory}
              timerStarted={timerStarted} // Передаем состояние таймера
              setTimerStarted={setTimerStarted} // Передаем функцию для изменения состояния таймера
            />
          )
        )}
      </div>
    </div>
  );
}

export default Modal;
