import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import Header from './Header';
import ContentContainer from './ContentContainer';
import EndMessage from './EndMessage';
import MenuModal from './MenuModal';
import Settings from './Settings';
import styles from '../styles/QuizPage.module.css';
import { setQuizData, markBlockAsUsed } from '../store/actions';

const QuizPage = ({ showMainMenu, handleNewGame }) => {
  const { mode } = useParams();
  const dispatch = useDispatch();
  const data = useSelector((state) => state.quiz.categories[mode]);

  useEffect(() => {
    if (data) {
      dispatch(setQuizData(data));
    }
  }, [data, dispatch]);

  const markBlock = (categoryName, blockId) => {
    dispatch(markBlockAsUsed(mode, categoryName, blockId));
  };

  return (
    <div className={styles.quiz_page}>
      <Header />
      <ContentContainer
        usedBlocks={data?.usedBlocks || {}}
        markBlockAsUsed={markBlock}
        data={data}
        mode={mode}
      />
      <EndMessage />
      <MenuModal showSettings={() => {}} showMainMenu={showMainMenu} />
      <Settings onClose={() => {}} />
    </div>
  );
};

export default QuizPage;
