import React from 'react';
import styles from './MenuModal.module.css';
import { useGameState } from '../../context/GameStateContext'; // Используем GameStateContext
import { useModal } from '../../context/ModalContext'; // Используем ModalContext
import { resetQuizStateAndCloseModal } from './menuModalUtils';
import NewGameButton from '../ButtonComponents/NewGameButton/NewGameButton';
import ContinueButton from '../ButtonComponents/ContinueButton/ContinueButton';
import SettingsButton from '../ButtonComponents/SettingsButton/SettingsButton';
import MainMenuButton from '../ButtonComponents/MainMenuButton/MainMenuButton';

function MenuModal({ showSettings, showMainMenu }) {
  const { isMenuVisible, closeMenuModal } = useModal(); // Используем ModalContext
  const { currentQuizId, setQuizStates } = useGameState(); // Используем GameStateContext

  return (
    <div
      id="menu-modal"
      className={styles.menuModal}
      style={{ display: isMenuVisible ? 'flex' : 'none', opacity: isMenuVisible ? 1 : 0 }}
    >
      <div className={styles.menuModalContent}>
        <NewGameButton
          onNewGame={() => resetQuizStateAndCloseModal(currentQuizId, setQuizStates, closeMenuModal)}
        />
        <ContinueButton onClick={closeMenuModal} />
        <SettingsButton onClick={showSettings} />
        <MainMenuButton onClick={showMainMenu} />
      </div>
    </div>
  );
}

export default MenuModal;
