import React from 'react';
import styles from '../styles/Item.module.css';

function Item({ block, categoryId, used, setSelectedBlock }) {
  return (
    <button
      className={`${styles.item} ${used ? styles.used : ''}`}
      onClick={() => {
        setSelectedBlock({ ...block, categoryId }); // Открываем Modal и передаем нужные данные
      }}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
