export function loadJsonFileCount() {
	const context = require.context('../data', false, /\.json$/);
	console.log('JSON files found:', context.keys().length);
	return context.keys().length;
  }
  
  export function loadJsonDataFiles() {
	const context = require.context('../data', false, /\.json$/);
	const files = context.keys().map(key => ({
	  ...context(key),
	  filename: key,
	}));
	console.log('Loaded JSON data files:', files);
	return files;
  }
  
  export function loadJsonDataByMode(mode) {
	console.log('Loading data for mode:', mode);
	const dataFiles = loadJsonDataFiles();
	console.log('All available data:', dataFiles);
	const foundFile = dataFiles.find(file => file.mode === mode);
	console.log('Data found for mode:', foundFile);
	return foundFile;
  }
