import React, { useState, useEffect } from 'react';
import SelectCategoryButton from '../SelectCategoryButton/SelectCategoryButton';
import styles from './SelectionMode.module.css';

function SelectionMode({
  block,
  categoryName,
  onSelectCategory, // Получаем функцию из родительского компонента
  onClose, // Добавляем функцию закрытия модального окна
}) {
  const [timerStarted, setTimerStarted] = useState(false);
  const [timerEnded, setTimerEnded] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => {
    setTimerStarted(false);
    setTimerEnded(false);
    setShowAnswer(false);
  }, [block]);

  const handleSelectCategoryInternal = () => {
    onSelectCategory(categoryName, block.id);  // Вызываем функцию для обработки выбора категории
    onClose(); // Закрываем модальное окно после выбора категории
  };

  return (
    <div className={styles.content}>
      <div className={styles.selectedInfo}>
        <span className={styles.infoCategoryName}>{categoryName}</span>
        <div className={styles.selectedNumber}>{block.id + 1}</div>
      </div>
      {/* Другие элементы */}
      <SelectCategoryButton onClick={handleSelectCategoryInternal} /> {/* Кнопка для выбора категории */}
    </div>
  );
}

export default SelectionMode;
