import React, { useState } from 'react';
import styles from './Settings.module.css';
import { useQuizContext } from '../../context/QuizContext';

function Settings({ onClose }) {
  const { setTheme, theme } = useQuizContext(); // Получаем метод для установки темы и текущую тему

  const handleDefaultTheme = () => {
    setTheme('default'); // Устанавливаем стандартную тему
  };

  const handleCustomTheme = () => {
    setTheme('custom'); // Устанавливаем кастомную тему
  };

  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <button className={styles.tab}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <button className={styles.tab}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>

      <div className={styles.buttonContainer}>
        <button className={styles.button} onClick={handleCustomTheme}>Custom</button>
        <button className={styles.button} onClick={handleDefaultTheme}>Default</button>
      </div>

      <button className={styles.closeButton} onClick={onClose}>Close</button>
    </div>
  );
}

export default Settings;
