import React, { useState, useEffect } from 'react';
import Header from './Header';
import ContentContainer from './ContentContainer';
import EndMessage from './EndMessage';
import MenuModal from './MenuModal';
import Settings from './Settings';
import styles from '../styles/QuizPage.module.css';
import { useQuizContext } from '../context/QuizContext';
import { loadJsonDataByMode } from '../utils/loadJsonData';

function QuizPage({ quizId }) {
  const { quizStates, updateQuizState, markBlockAsUsed, setShowQuizPage } = useQuizContext();
  const currentQuizState = quizStates[quizId] || {};
  const [data, setData] = useState(currentQuizState.data || null);
  const [isSettingsVisible, setIsSettingsVisible] = useState(false);

  useEffect(() => {
    console.log('Current Quiz ID:', quizId);
    console.log('Current Quiz State:', currentQuizState);

    if (!data && quizId) {
      const selectedMode = currentQuizState.mode; // Определяем режим из состояния викторины
      if (selectedMode) {
        console.log('Loading data for mode:', selectedMode);
        const selectedData = loadJsonDataByMode(selectedMode);
        if (selectedData) {
          setData(selectedData.categories);
          updateQuizState(quizId, { data: selectedData.categories });
        } else {
          console.log('No data found for mode:', selectedMode);
        }
      }
    }
  }, [data, quizId, currentQuizState, updateQuizState]);

  const handleBlockUse = (categoryName, blockId) => {
    markBlockAsUsed(quizId, categoryName, blockId);
  };

  const showSettings = () => {
    setIsSettingsVisible(true);
  };

  const hideSettings = () => {
    setIsSettingsVisible(false);
  };

  return (
    <div className={styles.quiz_page}>
      <Header />
      {data ? (
        <ContentContainer
          usedBlocks={currentQuizState.usedBlocks || {}}
          markBlockAsUsed={handleBlockUse}
          data={data}
          // mode пропс больше не передается
        />
      ) : (
        <div>No data available.</div>
      )}
      <EndMessage />
      <MenuModal showSettings={showSettings} showMainMenu={() => setShowQuizPage(false)} />
      {isSettingsVisible && <Settings onClose={hideSettings} />}
    </div>
  );
}

export default QuizPage;
