import React from 'react';
import styles from './Settings.module.css';

function Settings({ onClose }) {
  const handleThemeClick = () => {
    console.log("Тема кнопка нажата"); // Здесь будет логика изменения темы
  };

  const handleParametersClick = () => {
    console.log("Параметри кнопка нажата"); // Здесь будет логика изменения параметров
  };

  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        {/* Кнопки "Тема" и "Параметри" */}
        <button className={styles.tab} onClick={handleThemeClick}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <button className={styles.tab} onClick={handleParametersClick}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Close</button>
    </div>
  );
}

export default Settings;
