import React from 'react';
import PropTypes from 'prop-types';
import { useQuizContext } from '../../context/QuizContext'; // Импортируем контекст
import styles from './QuizCard.module.css';

function QuizCard({ startQuiz, mode }) {
  const { completedGames } = useQuizContext(); // Получаем количество завершенных игр из контекста

  return (
    <div className={styles.card}>
      <div className={styles.image}>
        {/* Placeholder for image */}
      </div>
      <div className={styles.details}>
        <h2>Quiz Mode {mode}</h2> {/* Displaying mode */}
        <div className={styles.bottomRow}>
        <p>Affiliation</p>
          <p className={styles.games}>Ігри: {completedGames}</p> {/* Отображаем количество игр */}
          <button className={styles.startButton} onClick={startQuiz}>
            Провести наживо
          </button>
        </div>
      </div>
    </div>
  );
}

QuizCard.propTypes = {
  startQuiz: PropTypes.func.isRequired,
  mode: PropTypes.number.isRequired,
};

export default QuizCard;
