import React from 'react';
import styles from './EndMessage.module.css';
import { FaAward } from 'react-icons/fa';
import NewGameButton from '../ButtonComponents/NewGameButton/NewGameButton';
import MainMenuButton from '../ButtonComponents/MainMenuButton/MainMenuButton';
import { resetQuizState } from './EndMessageUtils';

function EndMessage({ onNewGame, onMainMenu, currentQuizId, setQuizStates }) {
  // Логируем, что компонент рендерится с нужными данными
  console.log('EndMessage rendered with:', { currentQuizId, setQuizStates });

  const handleNewGame = () => {
    console.log('handleNewGame called');
    resetQuizState(currentQuizId, setQuizStates); // Сброс состояния викторины
    onNewGame(); // Старт новой игры
  };

  const handleMainMenu = () => {
    console.log('handleMainMenu called');
    resetQuizState(currentQuizId, setQuizStates); // Сброс состояния викторины
    console.log('State reset for Quiz ID:', currentQuizId);
    onMainMenu(); // Возврат в главное меню
    console.log('onMainMenu function called'); // Логируем вызов функции возврата в главное меню
  };

  return (
    <div id="end-message" className={styles.endMessage}>
      <div className={styles.endMessageContent}>
        <div className={styles.greetings}>Вітаю, ви завершили вікторину!</div>
        <div className={styles.awardIconContainer}>
          <FaAward className={styles.awardIcon} />
        </div>
        <div className={styles.buttons}>
          <NewGameButton onNewGame={handleNewGame} />
          <button className={styles.mainMenuButton} onClick={handleMainMenu}>Головне меню</button>
        </div>
      </div>
    </div>
  );
}

export default EndMessage;
