import React from 'react';
import styles from '../styles/Item.module.css';
import { handleClick } from '../utils/itemUtils';

function Item({ block, onClick, used }) {
  console.log(`Rendering Item with block ID: ${block.id}, used: ${used}`); // Добавляем лог для отладки

  return (
    <button
      className={`${styles.item} ${used ? styles.used : ''}`}
      onClick={() => {
        console.log(`Item clicked with block ID: ${block.id}`); // Лог при клике на блок
        handleClick(block, onClick);
      }}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
