import React, { createContext, useContext, useState, useEffect } from 'react';
import { loadJsonDataByMode } from '../utils/loadJsonData';

// Создаем контекст
const GameStateContext = createContext();

export function useGameState() {
  return useContext(GameStateContext);
}

export function GameStateProvider({ children }) {
  const [currentQuizId, setCurrentQuizId] = useState(() => {
    return localStorage.getItem('currentQuizId');
  });

  const [quizStates, setQuizStates] = useState(() => {
    const savedStates = localStorage.getItem('quizStates');
    return savedStates ? JSON.parse(savedStates) : {};
  });

  const [data, setData] = useState(() => {
    const savedData = localStorage.getItem('data');
    return savedData ? JSON.parse(savedData) : null;
  });

  const [completedGames, setCompletedGames] = useState(() => {
    const savedGames = localStorage.getItem('completedGames');
    return savedGames ? JSON.parse(savedGames) : 0;
  });

  const [selectedMode, setSelectedMode] = useState(null);
  const [showQuizPage, setShowQuizPage] = useState(() => {
    const savedState = localStorage.getItem('showQuizPage');
    return savedState === 'true';
  });

  useEffect(() => {
    if (currentQuizId) {
      const selectedData = loadJsonDataByMode(currentQuizId);
      const savedData = localStorage.getItem('data');
      if (selectedData && JSON.stringify(selectedData) !== savedData) {
        setData(selectedData);
        localStorage.setItem('data', JSON.stringify(selectedData));
      }
    }
  }, [currentQuizId]);

  useEffect(() => {
    if (selectedMode !== null) {
      localStorage.setItem('selectedMode', selectedMode);
    }
  }, [selectedMode]);

  useEffect(() => {
    localStorage.setItem('showQuizPage', showQuizPage.toString());
  }, [showQuizPage]);

  useEffect(() => {
    if (currentQuizId !== null) {
      localStorage.setItem('currentQuizId', currentQuizId);
    }
  }, [currentQuizId]);

  useEffect(() => {
    localStorage.setItem('completedGames', JSON.stringify(completedGames));
  }, [completedGames]);

  const startQuiz = (mode, uuid) => {
    setSelectedMode(mode);
    setCurrentQuizId(uuid);
    setShowQuizPage(true);
  };

  const updateQuizState = (uuid, newState) => {
    setQuizStates((prevStates) => {
      const updatedStates = {
        ...prevStates,
        [uuid]: {
          ...prevStates[uuid],
          ...newState,
        },
      };
      localStorage.setItem('quizStates', JSON.stringify(updatedStates));
      return updatedStates;
    });
  };

  return (
    <GameStateContext.Provider
      value={{
        currentQuizId,
        setCurrentQuizId,
        quizStates,
        setQuizStates,
        data,
        completedGames,
        setCompletedGames,
        selectedMode,
        setSelectedMode,
        showQuizPage,
        setShowQuizPage,
        startQuiz,
        updateQuizState,
      }}
    >
      {children}
    </GameStateContext.Provider>
  );
}
