import { configureStore, createSlice } from '@reduxjs/toolkit';
import thunk from 'redux-thunk';
import { loadJsonDataByMode } from '../utils/loadJsonData';

// Define the initial state and reducers using createSlice
const quizSlice = createSlice({
  name: 'quiz',
  initialState: {},
  reducers: {
    setQuizState: (state, action) => {
      state[action.payload.mode] = {
        ...state[action.payload.mode],
        ...action.payload.state,
      };
    },
    markBlockAsUsed: (state, action) => {
      if (!state[action.payload.mode]) {
        return; // or handle it appropriately
      }
      const modeState = state[action.payload.mode];
      if (!modeState.usedBlocks) {
        modeState.usedBlocks = {};
      }
      if (!modeState.usedBlocks[action.payload.categoryName]) {
        modeState.usedBlocks[action.payload.categoryName] = [];
      }
      modeState.usedBlocks[action.payload.categoryName].push(action.payload.blockId);
    },
  },
  extraReducers: (builder) => {
    builder.addCase(loadJsonDataByMode.fulfilled, (state, action) => {
      state[action.meta.arg] = { categories: action.payload };
    });
  },
});

// Export actions
export const { setQuizState, markBlockAsUsed } = quizSlice.actions;

// Create the Redux store
const store = configureStore({
  reducer: {
    quiz: quizSlice.reducer,
  },
  middleware: [thunk],
});

export default store;
