import React from 'react';
import styles from './NewGameButton.module.css';

function NewGameButton({ onNewGame }) {
  const handleNewGame = () => {
    // Уведомляем MenuModal о том, что началась новая игра
    onNewGame();
  };

  return (
    <button id="new-game-button" className={styles.newGameButton} onClick={handleNewGame}>
      Нова гра
    </button>
  );
}

export default NewGameButton;
