import React, { memo } from 'react';
import PropTypes from 'prop-types';
import { BUTTON_VARIANTS, BUTTON_SIZES, BUTTON_TEXTS } from './ButtonTypes';
import styles from './Button.module.css';

const Button = memo(function Button({
  variant = BUTTON_VARIANTS.PRIMARY,
  size = BUTTON_SIZES.MEDIUM,
  onClick,
  disabled = false,
  className,
  children,
  icon,
  ariaLabel,
  ...props
}) {
  const baseClassName = styles.button;
  const variantClassName = styles[variant];
  const sizeClassName = styles[size];
  const finalClassName = `${baseClassName} ${variantClassName} ${sizeClassName} ${className || ''}`.trim();

  const buttonText = children || BUTTON_TEXTS[variant];

  return (
    <button
      className={finalClassName}
      onClick={onClick}
      disabled={disabled}
      aria-label={ariaLabel || buttonText}
      {...props}
    >
      {icon && <span className={styles.icon}>{icon}</span>}
      {buttonText}
    </button>
  );
});

Button.propTypes = {
  variant: PropTypes.oneOf(Object.values(BUTTON_VARIANTS)),
  size: PropTypes.oneOf(Object.values(BUTTON_SIZES)),
  onClick: PropTypes.func.isRequired,
  disabled: PropTypes.bool,
  className: PropTypes.string,
  children: PropTypes.node,
  icon: PropTypes.node,
  ariaLabel: PropTypes.string
};

export { Button, BUTTON_VARIANTS, BUTTON_SIZES };
