import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import Sidebar from './Sidebar';
import styles from '../styles/App.module.css';
import { QuizProvider, useQuizContext } from '../context/QuizContext';

function App() {
  const { setQuizState } = useQuizContext();
  const [quizCount, setQuizCount] = useState(0); // Счётчик для генерации уникальных идентификаторов
  const navigate = useNavigate();

  const startQuiz = () => {
    const id = quizCount + 1; // Генерация нового уникального идентификатора
    setQuizState(id, { showQuizPage: true });
    setQuizCount(id); // Увеличиваем счётчик
    navigate(`/quiz/${id}`); // Переход на уникальную страницу QuizPage
  };

  return (
    <div className={styles.container}>
      <Routes>
        <Route
          path="/"
          element={
            <div>
              <Sidebar />
              <div className={styles.content}>
                <QuizCard startQuiz={startQuiz} />
                {/* Можно добавить больше QuizCard, если нужно */}
              </div>
            </div>
          }
        />
        {[...Array(quizCount).keys()].map((index) => (
          <Route
            key={index + 1}
            path={`/quiz/${index + 1}`}
            element={<QuizPage id={index + 1} />}
          />
        ))}
      </Routes>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <QuizProvider>
      <Router>
        <App />
      </Router>
    </QuizProvider>
  );
}
