// Action types
export const SET_QUIZ_DATA = 'SET_QUIZ_DATA';
export const MARK_BLOCK_AS_USED = 'MARK_BLOCK_AS_USED';

// Action creators
export const setQuizData = (data) => ({
  type: SET_QUIZ_DATA,
  payload: data,
});

export const markBlockAsUsed = (mode, categoryName, blockId) => ({
  type: MARK_BLOCK_AS_USED,
  payload: { mode, categoryName, blockId },
});

// Thunk action to load data from JSON
export const loadQuizData = () => async (dispatch) => {
  try {
    const response = await fetch('/path-to-your-json-file.json');
    const data = await response.json();
    dispatch(setQuizData(data));
  } catch (error) {
    console.error('Failed to load quiz data', error);
  }
};
