function Modal({
  block,
  onClose,
  selectedMode,
  markBlockAsUsed, // добавляем markBlockAsUsed
  timerStarted,
  timerEnded,
  showAnswer,
  setTimerStarted,
  handleTimerEnd,
  handleShowAnswer,
  handleForceStop,
}) {
  console.log('Modal opened with block:', block);
  const ModeComponent = modeComponents[selectedMode];

  if (!block) {
    return null;
  }

  const handleSelectCategory = () => {
    markBlockAsUsed(block.categoryId, block.id);
    updateQuizState(currentQuizId, {
      data: data.map((category) =>
        category.id === block.categoryId
          ? {
              ...category,
              blocks: category.blocks.map((b) =>
                b.id === block.id ? { ...b, used: true } : b
              ),
            }
          : category
      ),
    });
    onClose();
  };
  

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        {ModeComponent ? (
          <ModeComponent
            block={block}
            showAnswer={showAnswer}
            setTimerStarted={setTimerStarted}
            timerStarted={timerStarted}
            timerEnded={timerEnded}
            handleTimerEnd={handleTimerEnd}
            handleShowAnswer={handleShowAnswer}
            handleForceStop={handleForceStop}
            handleSelectCategory={handleSelectCategory} // добавляем обработчик
          />
        ) : (
          <div>Unknown mode</div>
        )}
      </div>
    </div>
  );
}

export default Modal;
