import React, { useState, useEffect } from 'react';
import StartTimerButton from '../StartTimerButton/StartTimerButton';
import Timer from '../Timer/Timer';
import SelectCategoryButton from '../SelectCategoryButton/SelectCategoryButton';
import ShowAnswerButton from '../ShowAnswerButton/ShowAnswerButton';
import HintButton from '../HintButton/HintButton';
import styles from './SelectionMode.module.css';

// Маппинг чисел и их текстовых эквивалентов на украинском языке
const numberWords = {
  "0": "нуль",
  "1": "один",
  "2": "два",
  "3": "три",
  "4": "чотири",
  "5": "п'ять",
  "6": "шість",
  "7": "сім",
  "8": "вісім",
  "9": "дев'ять",
  "10": "десять",
  "20": "двадцять",
  "30": "тридцять",
  "40": "сорок",
  "50": "п'ятдесят",
  "60": "шістдесят",
  "70": "сімдесят",
  "80": "вісімдесят",
  "90": "дев'яносто",
  "100": "сто"
};

// Функция преобразования чисел в текст
const numberToWords = (num) => {
  if (numberWords[num]) return numberWords[num];
  return num;
};

// Функция преобразования слов в числа
const wordsToNumber = (word) => {
  const foundEntry = Object.entries(numberWords).find(([key, value]) => value === word.toLowerCase());
  return foundEntry ? foundEntry[0] : word;
};

function SelectionMode({
  block,
  categoryName,
  showAnswer,
  setTimerStarted,
  timerStarted,
  timerEnded,
  handleTimerEnd,
  handleShowAnswer,
  handleSelectCategory,
  handleForceStop,
}) {
  const [answerShown, setAnswerShown] = useState(false);
  const [hintUsed, setHintUsed] = useState(false);
  const [highlightedOptions, setHighlightedOptions] = useState([]);
  const [displayText, setDisplayText] = useState(block.question); // Изначально показываем вопрос
  const [textFontSize, setTextFontSize] = useState(30); // Изначальный размер шрифта для вопроса

  useEffect(() => {
    setAnswerShown(false);
    setHintUsed(false);
    setHighlightedOptions([]);
    setTimerStarted(false);
    setDisplayText(block.question); // Сброс отображаемого текста при смене блока
    setTextFontSize(30); // Сброс размера шрифта при смене блока
  }, [block, setTimerStarted]);

  const handleForceStopInternal = () => {
    handleForceStop();
  };

  const handleShowAnswerInternal = () => {
    handleShowAnswer();
    setAnswerShown(true);

    // Изменение отображаемого текста на 'text' с переносом после символа '('
    const formattedText = formatText(block.text);
    setDisplayText(highlightCorrectAnswer(formattedText, block['correct answer'])); // Меняем отображаемый текст на отформатированный и выделяем правильный ответ

    // Проверка длины текста ответа и изменение размера шрифта
    const textLength = block.text.length;
    if (textLength <= 90) {
      setTextFontSize(22);
    } else if (textLength <= 110) {
      setTextFontSize(20);
    } else if (textLength <= 150) {
      setTextFontSize(18);
    } else {
      setTextFontSize(16); // Для текстов больше 150 символов уменьшаем шрифт до 16
    }

    const correctAnswerIndex = block.options.findIndex(
      (option) => option === block['correct answer']
    );

    const updatedOptions = block.options.map((option, index) => {
      if (index === correctAnswerIndex) {
        return '';
      }
      return styles.incorrectOption;
    });

    setHighlightedOptions(updatedOptions);
  };

  const handleHintInternal = () => {
    setHintUsed(true);

    const correctAnswerIndex = block.options.findIndex(
      (option) => option === block['correct answer']
    );

    let randomIndex;
    do {
      randomIndex = Math.floor(Math.random() * block.options.length);
    } while (randomIndex === correctAnswerIndex);

    const updatedOptions = block.options.map((option, index) => {
      if (index === correctAnswerIndex || index === randomIndex) {
        return '';
      }
      return styles.incorrectOption;
    });

    setHighlightedOptions(updatedOptions);
  };

  const handleStartTimerInternal = () => {
    setTimerStarted(true);
  };

  const handleSelectCategoryInternal = () => {
    handleSelectCategory(block.categoryId, block.id);
  };

  const getOptionFontSize = (text) => {
    const length = text.length;
    if (length <= 10) {
      return 25;
    } else if (length <= 22) {
      return 22;
    } else {
      return 18; // Оставляем размер шрифта по умолчанию
    }
  };

  // Функция для форматирования текста, добавляющая перенос строки после символа '('
  const formatText = (text) => {
    const index = text.indexOf('(');
    if (index !== -1) {
      return text.slice(0, index) + '\n' + text.slice(index);
    }
    return text;
  };

  // Функция для выделения правильного ответа цветом, включая проверку на числа
  const highlightCorrectAnswer = (text, correctAnswer) => {
    const exactMatchRegex = new RegExp(`(${correctAnswer})`, 'gi');
    const partialMatchRegex = new RegExp(`(${correctAnswer.slice(0, -2)})\\S*`, 'gi'); // Сравнение до последних двух символов

    // Проверяем на точное совпадение сначала
    if (exactMatchRegex.test(text)) {
      return text.replace(exactMatchRegex, `<span style="color: #e21b3c;">$1</span>`);
    }

    // Если точного совпадения нет, проверяем до предпоследнего символа
    if (partialMatchRegex.test(text)) {
      return text.replace(partialMatchRegex, `<span style="color: #e21b3c;">$&</span>`);
    }

    // Если нет совпадения, проверяем числовое совпадение
    const numericAnswer = wordsToNumber(correctAnswer);
    const wordAnswer = numberToWords(correctAnswer);

    const numericMatchRegex = new RegExp(`(${numericAnswer}|${wordAnswer})`, 'gi');
    if (numericMatchRegex.test(text)) {
      return text.replace(numericMatchRegex, `<span style="color: #e21b3c;">$&</span>`);
    }

    return text;
  };

  if (!block) {
    return <div>Loading...</div>;
  }

  return (
    <div className={styles.content}>
      <div className={styles.selectedInfo}>
        <span className={styles.infoCategoryName}>{categoryName}</span>
        <div className={styles.selectedNumber}>{block.id + 1}</div>
      </div>
      <div className={styles.wrapper}>
        <div className={styles.selectionMode}>
          <div
            className={styles.question}
            style={{ fontSize: `${textFontSize}px`, whiteSpace: 'pre-line' }}
            dangerouslySetInnerHTML={{ __html: displayText }}
          />
          <div className={styles.options}>
            {block.options.map((option, index) => (
              <div
                key={index}
                className={`${styles.option} ${highlightedOptions[index]}`}
                style={{ fontSize: `${getOptionFontSize(option)}px` }}
              >
                {option}
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className={styles.controlBlock}>
        {!timerStarted ? (
          <StartTimerButton onClick={handleStartTimerInternal} />
        ) : !timerEnded ? (
          <Timer
            key={block.id}
            duration={30}
            onEnd={handleTimerEnd}
            onForceStop={handleForceStopInternal}
          />
        ) : !answerShown ? (
          <div className={styles.buttonGroup}>
            <ShowAnswerButton onClick={handleShowAnswerInternal} />
            {!hintUsed && <HintButton onClick={handleHintInternal} />}
          </div>
        ) : (
          <SelectCategoryButton onClick={handleSelectCategoryInternal} />
        )}
      </div>
    </div>
  );
}

export default SelectionMode;
