export function loadJsonFileCount() {
    const context = require.context('../data', false, /\.json$/);
    return context.keys().length;
  }
  
  export function loadJsonDataFiles() {
    const context = require.context('../data', false, /\.json$/);
    return context.keys().map(key => ({
      ...context(key),
      filename: key,
    }));
  }
