import React, { useState, useEffect } from 'react';
import Header from './Header';
import ContentContainer from './ContentContainer';
import EndMessage from './EndMessage';
import MenuModal from './MenuModal';
import Settings from './Settings';
import styles from '../styles/QuizPage.module.css';
import { useQuizContext } from '../context/QuizContext';
import { loadJsonDataByMode } from '../utils/loadJsonData'; // Импорт функции

function QuizPage({ quizId }) {
  const { usedBlocks, setUsedBlocks, selectedMode, setShowQuizPage, quizStates, updateQuizState } = useQuizContext();
  const [isSettingsVisible, setIsSettingsVisible] = useState(false);
  const [data, setData] = useState(null);

  const currentQuizState = quizStates[quizId] || {}; // Получаем состояние для текущего uuid

  useEffect(() => {
    if (selectedMode && quizId) {
      const selectedData = loadJsonDataByMode(selectedMode);
      if (selectedData) {
        setData(selectedData.categories);
        updateQuizState(quizId, { data: selectedData.categories }); // Сохраняем данные в состоянии
      }
    }
  }, [selectedMode, quizId]);

  const markBlockAsUsed = (categoryName, blockId) => {
    const updatedUsedBlocks = { ...usedBlocks };
    if (!updatedUsedBlocks[categoryName]) {
      updatedUsedBlocks[categoryName] = [];
    }
    updatedUsedBlocks[categoryName].push(blockId);
    setUsedBlocks(updatedUsedBlocks);
    localStorage.setItem('usedBlocks', JSON.stringify(updatedUsedBlocks));
  };

  const showSettings = () => {
    setIsSettingsVisible(true);
  };

  const hideSettings = () => {
    setIsSettingsVisible(false);
  };

  return (
    <div className={styles.quiz_page}>
      <Header />
      <ContentContainer
        usedBlocks={currentQuizState.usedBlocks || []}
        markBlockAsUsed={markBlockAsUsed}
        data={data || currentQuizState.data} // Используем данные из состояния, если они есть
        mode={selectedMode}
      />
      <EndMessage />
      <MenuModal showSettings={showSettings} showMainMenu={() => setShowQuizPage(false)} />
      {isSettingsVisible && <Settings onClose={hideSettings} />}
    </div>
  );
}

export default QuizPage;
