// Item.js
import React from 'react';
import styles from './Item.module.css';

function Item({ block, categoryId, onBlockSelect, isUsed }) {
  const handleClick = () => {
    onBlockSelect({ ...block, categoryId });
  };

  return (
    <button
      className={`${styles.item} ${isUsed ? styles.used : ''}`}
      onClick={handleClick}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
