import React, { useState, useEffect } from 'react';
import Item from '../Item/Item';
import styles from './CategoryRow.module.css';

function CategoryRow({ category, onBlockSelect }) {
  // Состояние для отслеживания измененных блоков
  const [allBlocksUsed, setAllBlocksUsed] = useState(false);

  useEffect(() => {
    // Проверяем, изменены ли все блоки
    const allUsed = category.blocks.every(block => block.isUsed);
    setAllBlocksUsed(allUsed);
  }, [category.blocks]);

  return (
    <div className={styles.categoryRow}>
      {/* Изменение стиля категории в зависимости от состояния всех блоков */}
      <div className={`${styles.categoryName} ${allBlocksUsed ? styles.usedCategory : ''}`}>
        {category.name}
      </div>
      <div className={styles.items}>
        {category.blocks.map((block) => (
          <Item
            key={`${category.id}-${block.id}`}
            block={block}
            categoryId={category.id}
            onBlockSelect={() => onBlockSelect(block, category)}
          />
        ))}
      </div>
    </div>
  );
}

export default CategoryRow;
