import React from 'react';
import styles from './Item.module.css';
import { useGameState } from '../../context/GameStateContext'; // Заменили контекст
import { checkIfUsed, handleBlockClick } from './itemUtils';

function Item({ block, categoryId, onBlockSelect }) {
  const { quizStates, currentQuizId } = useGameState(); // Используем GameStateContext
  const isUsed = checkIfUsed(quizStates, currentQuizId, categoryId, block.id);

  return (
    <button
      className={`${styles.box} ${isUsed ? styles.used : ''}`}
      onClick={() => handleBlockClick(block, categoryId, onBlockSelect)}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
