import React from 'react';
import styles from './MenuModal.module.css';
import NewGameButton from '../NewGameButton/NewGameButton';
import ContinueButton from '../ContinueButton/ContinueButton';
import SettingsButton from '../SettingsButton/SettingsButton';
import MainMenuButton from '../MainMenuButton/MainMenuButton';

function MenuModal({ showSettings, showMainMenu, onNewGame, isVisible, closeMenuModal }) {
  if (!isVisible) return null; // Если модальное окно не активно, не отображаем его

  return (
    <div className={styles.menuModal}>
      <div className={styles.menuModalContent}>
        <NewGameButton onNewGame={() => {
          onNewGame();
          closeMenuModal(); // Закрываем модальное окно после начала новой игры
        }} />
        <ContinueButton onClick={closeMenuModal} /> {/* Закрытие модального окна */}
        <SettingsButton onClick={showSettings} />
        <MainMenuButton onClick={showMainMenu} />
      </div>
    </div>
  );
}

export default MenuModal;
