import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import QuizPage from '../QuizPage/QuizPage';
import QuizCard from '../QuizCard/QuizCard';
import Sidebar from '../Sidebar/Sidebar';
import { useGameState } from '../../context/GameStateContext';
import { useModal } from '../../context/ModalContext';
import styles from './App.module.css';
import { startQuizHandler } from './appUtils';

function App() {
  const { data, setCurrentQuizId, quizStates } = useGameState();
  const { isMenuVisible, toggleMenuModal } = useModal();
  const [quizData, setQuizData] = useState([]);

  useEffect(() => {
    setQuizData(data); // Подставляем данные, загруженные из GameStateContext
  }, [data]);

  return (
    <Router>
      <div className={styles.container}>
        {!isMenuVisible && <Sidebar />}
        <div className={styles.content_wraper}>
          <div className={styles.content}>
            <Routes>
              <Route
                path="/"
                element={
                  quizData.map((data) => (
                    <QuizCard
                      key={data.uuid}
                      startQuiz={() => startQuizHandler(data.mode, data.uuid, setCurrentQuizId)}
                      mode={data.mode}
                      name={data.name}
                      categories={data.categories || []}
                    />
                  ))
                }
              />
            </Routes>
          </div>
        </div>
        <QuizPage />
      </div>
    </Router>
  );
}

export default App;
