import React from 'react';
import styles from '../styles/Item.module.css';
import { useQuizContext } from '../context/QuizContext';

function Item({ block, categoryId, used }) {
  const { markBlockAsUsed, currentQuizId } = useQuizContext();

  console.log(`Rendering Item with block ID: ${block.id}, used: ${used}`);

  return (
    <button
      className={`${styles.item} ${used ? styles.used : ''}`}
      onClick={() => {
        console.log(`Item clicked with block ID: ${block.id}`);
        markBlockAsUsed(currentQuizId, categoryId, block.id); // Изменение состояния через контекст
      }}
    >
      {block.id + 1}
    </button>
  );
}

export default Item;
