import React, { useEffect, useRef } from "react";
import confetti from "canvas-confetti";

const ConfettiAnimation = ({ isRunning }) => {
  const intervalRef = useRef(null);

  useEffect(() => {
    const defaults = {
      spread: 400,
      ticks: 100, // Увеличиваем количество "тиков" для более продолжительной анимации
      gravity: 0.2, // Уменьшаем силу тяжести для более плавного падения
      decay: 0.95, // Медленное затухание анимации
      startVelocity: 15, // Уменьшаем начальную скорость для плавности
      shapes: ["star", "circle"],
      colors: ['#8A2BE2', '#9400D3', '#DDA0DD', '#9370DB', '#BA55D3', '#EE82EE', '#DA70D6', '#D8BFD8'],
    };

    const shoot = () => {
      const randomX = Math.random(); // Случайное значение для X (от 0 до 1)
      const randomY = Math.random(); // Случайное значение для Y (от 0 до 1)

      confetti({
        ...defaults,
        particleCount: 30, // Уменьшаем количество частиц для более плавного эффекта
        scalar: 1.2,
        origin: { x: randomX, y: randomY }, // Появление конфетти в случайной позиции
      });

      confetti({
        ...defaults,
        particleCount: 15, // Уменьшаем количество частиц для второго набора
        scalar: 0.8,
        origin: { x: randomX, y: randomY },
      });
    };

    if (isRunning) {
      // Запускаем анимацию с большим интервалом для более плавного эффекта
      intervalRef.current = setInterval(shoot, 1000); // Увеличиваем интервал до 1 секунды
    } else {
      // Останавливаем анимацию, если isRunning = false
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
        intervalRef.current = null;
      }
    }

    // Очищаем интервал при размонтировании компонента
    return () => {
      if (intervalRef.current) {
        clearInterval(intervalRef.current);
      }
    };
  }, [isRunning]); // Слушаем изменения состояния isRunning

  return <div />;
};

export default ConfettiAnimation;
