import React, { useState, useEffect } from 'react';
import Header from './Header';
import ContentContainer from './ContentContainer';
import EndMessage from './EndMessage';
import MenuModal from './MenuModal';
import Settings from './Settings';
import styles from '../styles/QuizPage.module.css';
import { useQuizContext } from '../context/QuizContext';
import { loadJsonDataByMode } from '../utils/loadJsonData'; // Импорт функции

function QuizPage({ mode, quizId }) {
  const { quizStates, updateQuizState } = useQuizContext();
  const [data, setData] = useState(null);

  useEffect(() => {
    if (mode && quizId) {
      const selectedData = loadQuizDataByModeAndId(mode, quizId);
      if (selectedData) {
        setData(selectedData.categories);
      }
    }
  }, [mode, quizId]);

  const handleUpdateState = (newState) => {
    updateQuizState(quizId, newState);
  };

  // Извлечение текущего состояния викторины из контекста
  const currentQuizState = quizStates[quizId] || {};

  return (
    <div className={styles.quiz_page}>
      <Header />
      <ContentContainer
        usedBlocks={currentQuizState.usedBlocks || []}
        markBlockAsUsed={(categoryName, blockId) => {
          handleUpdateState({ usedBlocks: [...currentQuizState.usedBlocks, blockId] });
        }}
        data={data}
        mode={mode}
      />
      <EndMessage />
      <MenuModal showSettings={() => handleUpdateState({ settingsVisible: true })} showMainMenu={() => setShowQuizPage(false)} />
      {currentQuizState.settingsVisible && <Settings onClose={() => handleUpdateState({ settingsVisible: false })} />}
    </div>
  );
}


export default QuizPage;
