import React from 'react';
import Item from './Item';
import styles from '../styles/CategoryRow.module.css';
import { useQuizContext } from '../context/QuizContext';

function CategoryRow({ category, setSelectedBlock }) {
  const { quizStates, currentQuizId } = useQuizContext();
  const usedBlocks = quizStates[currentQuizId]?.usedBlocks || {};
  const categoryUsed = usedBlocks[category.id]?.length === category.blocks.length;

  return (
    <div className={styles.categoryRow} style={{ backgroundColor: categoryUsed ? 'lightgray' : '' }}>
      <div className={styles.categoryName}>{category.name}</div>
      <div className={styles.items}>
        {category.blocks.map((block) => (
          <Item
            key={`${category.id}-${block.id}`}
            block={block}
            categoryId={category.id}
            used={usedBlocks[category.id]?.includes(block.id)}
            setSelectedBlock={setSelectedBlock}
          />
        ))}
      </div>
    </div>
  );
}

export default CategoryRow;
