import React from 'react';
import styles from './Settings.module.css';

function Settings({ onClose }) {
  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <button className={styles.tab}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <button className={styles.tab}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>
      <div className={styles.content}>
        {/* Раздел для выбора темы */}
        <div className={styles.themeSection}>
          <h3 className={styles.sectionTitle}>Выбор темы</h3>
          <div className={styles.buttonGroup}>
            <button className={styles.themeButton}>Custom</button>
            <button className={styles.themeButton}>Default</button>
          </div>
        </div>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Close</button>
    </div>
  );
}

export default Settings;
