// src/utils/loadData.js

// Функция для загрузки количества JSON файлов
export function loadFileCount() {
  const context = require.context('../data', false, /\.json$/);
  return context.keys().length;
}

// Функция для загрузки всех JSON файлов как массив объектов
export function loadJsonDataFiles() {
  const context = require.context('../data', false, /\.json$/);
  return context.keys().map(key => ({
    ...context(key),
    filename: key,
  }));
}

// Функция для поиска данных по UUID
export function loadJsonDataByMode(uuid) {
  const dataFiles = loadJsonDataFiles();
  return dataFiles.find(file => file.uuid === uuid);
}
