import React, { useState, useEffect } from 'react';
import styles from './Settings.module.css';

function Settings({ onClose }) {
  const [theme, setTheme] = useState('default');

  useEffect(() => {
    // Проверяем локальное хранилище для сохраненной темы
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  const handleThemeChange = (newTheme) => {
    setTheme(newTheme);
    localStorage.setItem('theme', newTheme);
  };

  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <button className={styles.tab}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <div className={styles.themeButtons}>
          <button 
            className={`${styles.themeButton} ${theme === 'default' ? styles.active : ''}`} 
            onClick={() => handleThemeChange('default')}
          >
            Дефолт
          </button>
          <button 
            className={`${styles.themeButton} ${theme === 'custom' ? styles.active : ''}`} 
            onClick={() => handleThemeChange('custom')}
          >
            Пользовательская
          </button>
        </div>
        <button className={styles.tab}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Закрыть</button>
    </div>
  );
}

export default Settings;
