import React, { useState } from 'react';
import styles from '../styles/Modal.module.css';
import Timer from './Timer';

function Modal({ block, onClose }) {
  const [timerEnded, setTimerEnded] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  if (!block) {
    return null;
  }

  const handleTimerEnd = () => {
    setTimerEnded(true);
  };

  const handleShowAnswer = () => {
    setShowAnswer(true);
  };

  const handleSelectCategory = () => {
    onClose();
  };

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        <div className={styles.selectedInfo}>
          <span className={styles.infoCategoryName}>{block.categoryName}</span>
          <div className={styles.selectedNumber}>{block.id + 1}</div>
        </div>
        <div className={styles.content}>
          <h2>{block.question}</h2>
          {!showAnswer && (
            <>
              {timerEnded ? (
                <Timer duration={30} onEnd={handleShowAnswer} />
              ) : (
                <button className={styles.startTimerButton} onClick={handleTimerEnd}>
                  <img src="./images/refresh-ccw-clock-svgrepo-com.svg" alt="Start Timer" className={styles.startButtonIcon} />
                </button>
              )}
            </>
          )}
          {showAnswer && (
            <>
              <p className={styles.answer}>{block.answer}</p>
              <p className={styles.subAnswer}>{block.subAnswer}</p>
              <button className={styles.selectCategoryButton} onClick={handleSelectCategory}>
                Обрати категорію
              </button>
            </>
          )}
        </div>
      </div>
      {timerEnded && !showAnswer && (
        <button className={styles.showAnswerButton} onClick={handleShowAnswer}>
          Показати відповідь
        </button>
      )}
    </div>
  );
}

export default Modal;
