import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import QuizPage from './QuizPage';
import QuizCard from './QuizCard';
import Sidebar from './Sidebar';
import styles from '../styles/App.module.css';
import { loadFileCount, loadJsonDataFiles } from '../utils/loadData';
import { handleShowQuizPage, handleShowMainMenu, handleNewGame } from '../utils/appUtils';

function App() {
  const [showQuizPage, setShowQuizPage] = useState(() => {
    const savedState = localStorage.getItem('showQuizPage');
    return savedState === 'true';
  });
  const [fileCount, setFileCount] = useState(0);
  const [selectedUuid, setSelectedUuid] = useState(null);
  const [quizData, setQuizData] = useState([]);

  useEffect(() => {
    const count = loadFileCount();
    setFileCount(count);

    const dataFiles = loadJsonDataFiles(); // Загружаем все файлы данных
    setQuizData(dataFiles); // Сохраняем их в состоянии
  }, []);

  const startQuiz = (uuid) => {
    setSelectedUuid(uuid);
    handleShowQuizPage(setShowQuizPage);
  };

  return (
    <Router>
      <div className={styles.container}>
        {!showQuizPage && <Sidebar />}
        <div className={showQuizPage ? styles.hidden : styles.content}>
          <Routes>
            <Route
              path="/"
              element={
                !showQuizPage ? (
                  quizData.map((quiz) => (
                    <QuizCard
                      key={quiz.uuid}
                      startQuiz={() => startQuiz(quiz.uuid)} // Начинаем викторину с соответствующим UUID
                      uuid={quiz.uuid} // Передаем UUID в QuizCard
                    />
                  ))
                ) : null
              }
            />
          </Routes>
        </div>
        {showQuizPage && (
          <div className={styles.fullscreen}>
            <QuizPage
              uuid={selectedUuid} // Передаем выбранный UUID в QuizPage
              showMainMenu={() => handleShowMainMenu(setShowQuizPage)}
              handleNewGame={() => handleNewGame(setShowQuizPage)}
            />
          </div>
        )}
      </div>
    </Router>
  );
}

export default App;
