import { SET_QUIZ_DATA, MARK_BLOCK_AS_USED } from './actions';

const initialState = {
  categories: {},
};

const quizReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_QUIZ_DATA:
      return {
        ...state,
        categories: action.payload,
      };
    case MARK_BLOCK_AS_USED:
      const { mode, categoryName, blockId } = action.payload;
      return {
        ...state,
        [mode]: {
          ...state[mode],
          usedBlocks: {
            ...state[mode].usedBlocks,
            [categoryName]: [
              ...(state[mode].usedBlocks[categoryName] || []),
              blockId,
            ],
          },
        },
      };
    default:
      return state;
  }
};

export default quizReducer;
