import React, { useState, useEffect } from 'react';
import QAMode from '../QAMode/QAMode';
import SelectionMode from '../SelectionMode/SelectionMode';
import styles from './Modal.module.css';

const modeComponents = {
  1: QAMode,
  2: SelectionMode,
};

function Modal({
  block,
  categoryName,
  onClose,
  selectedMode,
  onSelectCategory,
}) {
  const ModeComponent = modeComponents[selectedMode];

  const [timerStarted, setTimerStarted] = useState(false);
  const [timerEnded, setTimerEnded] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  useEffect(() => {
    setTimerStarted(false);
    setShowAnswer(false);
    setTimerEnded(false);
  }, [block]);

  useEffect(() => {
    console.log('Modal received selectedMode:', selectedMode);
    console.log('Modal received ModeComponent:', ModeComponent);
  }, [selectedMode, ModeComponent]);

  if (!block) {
    return null;
  }

  if (!ModeComponent) {
    console.error(`Unknown mode: ${selectedMode}`);
    return <div>Unknown mode</div>;
  }

  return (
    <div className={`${styles.modal} ${styles.show}`} onClick={onClose}>
      <div className={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <span className={styles.closeButton} onClick={onClose}>&times;</span>
        <ModeComponent
          block={block}
          categoryName={categoryName}
          showAnswer={showAnswer}
          setTimerStarted={setTimerStarted}
          timerStarted={timerStarted}
          timerEnded={timerEnded}
          handleTimerEnd={() => setTimerEnded(true)}
          handleShowAnswer={() => setShowAnswer(true)}
          handleSelectCategory={() => onSelectCategory(block.categoryId, block.id)}
          handleForceStop={() => setTimerEnded(true)}
        />
      </div>
    </div>
  );
}

export default Modal;
