import { useState } from 'react';

export function useModalLogic(block, markBlockAsUsed, onClose) {
  const [timerStarted, setTimerStarted] = useState(false);
  const [timerEnded, setTimerEnded] = useState(false);
  const [showAnswer, setShowAnswer] = useState(false);

  if (!block) {
    console.warn('useModalLogic: block is null or undefined');
    return {
      timerStarted: false,
      timerEnded: false,
      showAnswer: false,
      setTimerStarted: () => {},
      handleTimerEnd: () => {},
      handleShowAnswer: () => {},
      handleSelectCategory: () => {},
      handleForceStop: () => {},
    };
  }

  console.log('useModalLogic: block is defined, initializing logic', block);

  const handleTimerEnd = () => {
    console.log('handleTimerEnd called');
    setTimerEnded(true);
  };

  const handleShowAnswer = () => {
    console.log('handleShowAnswer called');
    setShowAnswer(true);
  };

  const handleSelectCategory = () => {
    console.log('handleSelectCategory called with:', block.categoryId, block.id);
    markBlockAsUsed(block.categoryId, block.id);
    onClose();
  };

  const handleForceStop = () => {
    console.log('handleForceStop called');
    setTimerEnded(true);
    setShowAnswer(true);
  };

  return {
    timerStarted,
    timerEnded,
    showAnswer,
    setTimerStarted,  // Возвращаем setTimerStarted здесь
    handleTimerEnd,
    handleShowAnswer,
    handleSelectCategory,
    handleForceStop,
  };
}
