import React, { useState } from 'react';
import styles from './Settings.module.css';

function Settings({ onClose, autoStartTimer, setAutoStartTimer }) {
  const handleSwitchChange = () => {
    setAutoStartTimer(!autoStartTimer);
  };

  return (
    <div className={styles.settings}>
      <div className={styles.sidebar}>
        <button className={styles.tab}>
          <span className={styles.tabText}>Тема</span>
        </button>
        <button className={styles.tab}>
          <span className={styles.tabText}>Параметри</span>
        </button>
      </div>
      <div className={styles.parameters}>
        <label>
          <input
            type="checkbox"
            checked={autoStartTimer}
            onChange={handleSwitchChange}
          />
          Автоматический запуск таймера
        </label>
      </div>
      <button className={styles.closeButton} onClick={onClose}>Close</button>
    </div>
  );
}

export default Settings;
