import React, { createContext, useContext, useState } from 'react';

// Создаем контекст
const QuizContext = createContext();

// Хук для доступа к контексту
export function useQuizContext() {
  return useContext(QuizContext);
}

// Провайдер контекста
export function QuizProvider({ children }) {
  const [showQuizPage, setShowQuizPage] = useState(() => {
    const savedState = localStorage.getItem('showQuizPage');
    return savedState === 'true';
  });
  const [selectedMode, setSelectedMode] = useState(null);
  const [currentQuizId, setCurrentQuizId] = useState(null); // Добавляем состояние для текущего uuid
  const [usedBlocks, setUsedBlocks] = useState(() => {
    const saved = localStorage.getItem('usedBlocks');
    return saved ? JSON.parse(saved) : {};
  });

  // Дополнительное состояние для управления каждой викториной по uuid
  const [quizStates, setQuizStates] = useState({});

  const updateQuizState = (uuid, newState) => {
    setQuizStates(prevStates => ({
      ...prevStates,
      [uuid]: {
        ...prevStates[uuid],
        ...newState,
      },
    }));
  };

  return (
    <QuizContext.Provider value={{
      showQuizPage,
      setShowQuizPage,
      selectedMode,
      setSelectedMode,
      currentQuizId, // Передаем currentQuizId
      setCurrentQuizId, // Передаем setCurrentQuizId
      usedBlocks,
      setUsedBlocks,
      quizStates,
      updateQuizState
    }}>
      {children}
    </QuizContext.Provider>
  );
}
