import React from 'react';
import PropTypes from 'prop-types';
import { useGameState } from '../../context/GameStateContext'; // Используем GameStateContext
import styles from './QuizCard.module.css';
import quizImage from '../../images/quizpng.png';
import { ReactComponent as PCImage } from '../../images/PC_horizontal_1line_color.svg';

function QuizCard({ mode, name, categories, uuid }) {
  const { completedGames, startQuiz } = useGameState(); // Используем startQuiz для запуска викторины

  // Подсчет общего количества вопросов
  const totalQuestions = categories && Array.isArray(categories)
    ? categories.reduce((acc, category) => acc + (category.blocks?.length || 0), 0)
    : 0;

  return (
    <div className={styles.card}>
      <div className={styles.image}>
        <img src={quizImage} alt="Quiz Image" className={styles.imageContent} />
        <div className={styles.questionCount}>{totalQuestions} запитань</div>
      </div>
      <div className={styles.details}>
        <h2>{name}</h2> {/* Отображаем название викторины */}
        <div className={styles.bottomRow}>
          <PCImage className={styles.affiliationIcon} />
          <p className={styles.games}>Ігри: {completedGames}</p>
          <button className={styles.startButton} onClick={() => startQuiz(mode, uuid)}>
            Провести наживо
          </button>
        </div>
      </div>
    </div>
  );
}

QuizCard.propTypes = {
  mode: PropTypes.number.isRequired,
  name: PropTypes.string.isRequired,
  categories: PropTypes.array.isRequired,
  uuid: PropTypes.string.isRequired,
};

export default QuizCard;
