import React from 'react';
import Item from '../Item/Item';
import styles from './CategoryRow.module.css';

function CategoryRow({ category, onBlockSelect, categoryIndex }) {
  return (
    <>
      <div className={styles.categoryName} style={{ gridRow: categoryIndex + 2 }}>{category.name}</div>
      {category.blocks.map((block, blockIndex) => (
        <div style={{ gridColumn: blockIndex + 2, gridRow: categoryIndex + 2 }}>
          <Item
            key={`${category.id}-${block.id}`}
            block={block}
            categoryId={category.id}
            onBlockSelect={onBlockSelect}
          />
        </div>
      ))}
    </>
  );
}

export default CategoryRow;
